
public class Teste2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1, num2;
		
		
		num1 = 9;
		
		num2 = 8;
		
		int soma, sub, mult, div, res;
		
		soma = num1 + num2;
		
		sub = num1 - num2;
		
		mult = num1 * num2;
		
		div = num1 / num2;
		
		res = num1 % num2;
		
		System.out.println("A soma dos valores é: " + soma);
		System.out.println("A subtração dos valores é: " + sub);
		System.out.println("A multiplicação dos valores é: " + mult);
		System.out.println("A divisão dos valores é: " + div);
		System.out.println("O resto é: " + res);
		
				
	
	
	
	
	
	
	}
	
	
	

}
